(()=>{"use strict";(t=>{const e=document.getElementById("timer-hours"),n=document.getElementById("timer-minutes"),o=document.getElementById("timer-seconds");let m=setInterval((()=>{let t=(()=>{let t=(new Date("8 april 2024").getTime()-(new Date).getTime())/1e3;return{timeRemaining:t,hours:Math.floor(t/60/60),minutes:Math.floor(t/60%60),seconds:Math.floor(t%60)}})();const s=t=>t<=9?"0"+t:t;t.timeRemaining<=0?(e.textContent="00",n.textContent="00",o.textContent="00",clearInterval(m)):(e.textContent=s(t.hours),n.textContent=s(t.minutes),o.textContent=s(t.seconds))}),1e3)})()})();